// tag-cloud.js
// ============

// Chmura tagw.

require(['jquery'], function ($) {

"use strict";

$(document).ready(function () {
	var max_counter = 0,
    i = 0,
    min_counter, avg;

  $('.tags > ul > li').each(function () {
    // Zbieramy wartoci counterw dla kadego taga.
    var count = parseInt($(this).attr('data-counter'), 10);
    if (count > max_counter) {
      max_counter = count;
    }
    if (min_counter === undefined || count < min_counter) {
      min_counter = count;
    }
    i++;
  });

  $('.tags > ul > li').each(function () {
    // Kolejna ptla - przyporzdkujemy klasy na podstawie
    // zebranych wczeniej wartoci.
    var count = parseInt($(this).attr('data-counter'), 10);

    if (count <= max_counter / 5) {
      $(this).addClass('tag1');
    } else if (count <= max_counter / 4) {
      $(this).addClass('tag2');
    } else if (count <= max_counter / 3) {
      $(this).addClass('tag3');
    } else if (count <= max_counter / 2) {
      $(this).addClass('tag4');
    } else {
      $(this).addClass('tag5');
    }
  });
});

});

define("js/modules/common/tag-cloud", function(){});

