// abuse-report.js
// ===============
//
// Allow registered users to send abuse reports related to content.
//
// Tworzc okno, przekazujemy do konstruktora obiekt z podstawowymi parametrami,
// ktre staj si naszymi 'defaultowymi' wartociami. Obiekt powinien zawiera:
//
//   - id      => id obiektu, ktry oznaczamy (object_pk)
//   - content => id dla typu zawartoci (ContentType) oznaczanego obiektu
//
// TODO: Warto pomyle nad przechwytywaniem bdw z serwera.
//

define('js/modules/utils/abuse-window',['jquery',
        'underscore',
        'js/modules/ui/ui',
        'js/modules/utils/utils',
        'bootstrap'],

function ($, _, ui, utils) {

    "use strict";
    
    var AbuseWindow = function (targetData) {
        this.data = targetData || {'id': 0, 'content': 0};
        this.template = _.template($('#abuse-modal-tpl').html());
        this.$el = $(document.createElement('div'));
        this.$el
            .addClass('modal fade')
            .html(this.template(this.data))
            .modal({'show':false});

        this.$form = this.$el.find('form:first');

        this.$el.find('.submit-btn').on('click', function (e) {
            e.preventDefault();
            this.$form.trigger('submit');
        }.bind(this));

        this.$form.on('submit', function (e) {
            e.preventDefault();
            this.submit();
        }.bind(this));
    };
    
    AbuseWindow.prototype.open = function () {
        this.$el.modal('show');
    };
    
    AbuseWindow.prototype.close = function () {
        this.$el.modal('hide');
    };
    
    AbuseWindow.prototype.submit = function () {
        var data = this.$form.serializeArray();
        $.ajaxSetup({
            headers: {'X-CSRFToken': utils.getCookie('csrftoken')}
        });
        $.post('/rest/reports/', data, function () {
            this.close();
            ui.message.success(gettext("Report sent"));
        }.bind(this));
    };
    
    return AbuseWindow;
});
