// bouncy-navigation.js
// ====================

// Dostosowana do projektu wersja pluginu. Patrz:
// http://codyhouse.co/gem/bouncy-navigation/

define('js/modules/bouncy-navigation/bouncy-navigation',['jquery',
			  'underscore'],

function ($, _) {
"use strict";

// Funkcja pozwalajca nam uniezaleni animacj przygotowan
// przez autorw pluginu i wykorzysta na rnych elementach.
// Oczywicie, elementy musz si zgadza z budowpluginu.
//
// @param { jQuery.DomElement } Okno modala do otwarcia/zamknicia
// @param { jQuery.DomElement } Przycisk uruchamiajcy animacj menu
// @param { Boolean }	true otwiera menu, false zamyka
// @param { Function } Callback do wywoania po zakoczeniu animacji

function triggerAnimation ($modal, $trigg, open, fn) {
	//toggle list items animation
	open = (!_.isUndefined(open)) ? open : true;
	$modal
		.toggleClass('fade-in', open)
		.toggleClass('fade-out', !open)
		.find('li:last-child').one('webkitAnimationEnd oanimationend msAnimationEnd animationend',
			function () {
				$modal.toggleClass('is-visible', open);
				if(!open) $modal.removeClass('fade-out');
				if (_.isFunction(fn)) fn();
			}
		);
	
	//check if CSS animations are supported... 
	if($trigg.parents('.no-csstransitions').length > 0 ) {
		$modal.toggleClass('is-visible', open);
		if (_.isFunction(fn)) fn();
	}
}

// Nawigacja musi zosta wywoana na $(document).ready.
// Inaczej nie zadziaa.

function BouncyNavigation ($modal, $trigger) {
	this.is_bouncy_nav_animating = false;
	this.$modal = $modal;
	this.$trigger = $trigger;
	this.initialize();
}

BouncyNavigation.prototype._trigger = function (open) {
	var flag = this.is_bouncy_nav_animating;
	if (!this.is_bouncy_nav_animating) {
		triggerAnimation(this.$modal, this.$trigger, open,
			function () {
				flag = false;
			}
		);
	}
};

BouncyNavigation.prototype.initialize = function () {
	this.$trigger.on('click', function (e) {
		e.preventDefault();
		this._trigger(true);
	}.bind(this));
	this.$modal.find('.cd-close').on('click', function (e) {
		e.preventDefault();
		this._trigger(false);
	}.bind(this));
	this.$modal.on('click', function (e) {
		if($(e.target).is('.cd-bouncy-nav-modal')) {
			this._trigger(false);
		}
	}.bind(this));
};

return BouncyNavigation;

});

//
// bouncy-menu.js
// ==============

// Skrypt pozwalajcy nam automatycznie uzupenia menu na podstawie
// listy lokalizacji obserwowanych przez uytkownika.

require(['jquery',
				 'underscore'],

function ($, _, utils) {

"use strict";

var url = '/api-userspace/locations/';
var tpl = _.template('<option value="<%= slug %>"><%= name %></option>');

// May helper, ktry skada url
//
// @param { String } Slug wybranej lokalizacji
// @param { String } Element dla url-a kierujcy do typu zawartoci.

function createUrl (slug, content) {
	return ("/{slug}/{content}/create/")
		.replace(/{slug}/g, slug)
		.replace(/{content}/g, content);
}

// Zmieniamy aktywn lokalizacj

function switchOptions (e) {
	$('.bouncy-option').each(function () {
		$(this).attr('href', createUrl(
			$(e.currentTarget).val(),
			$(this).attr('data-content'))
		);
	});
}

// Tworzy automatycznie menu
//
// @param { jQuery.DomElement } Element select w jQuery

function createMenu ($select) {
	$.get(url, function (locations) {
		var slug = $select.attr('data-location'), $option;

		// Jeeli jestemy w aktywnej lokacji, ustawiamy j na wybran,
		// w przeciwnym wypadku bierzemy pierwsz z pobranej listy.
		if (_.isUndefined(slug) && locations.length) {
			slug = locations[0].slug;
		}

		_.each(locations, function (location) {

			// Jestemy na podstronie lokalizacji i jaka jest wybrana.
			// W tym przypadku opcja ju istnieje i nie robimy drugiej.
			if (location.slug === $select.attr('data-location')) {
				return true;
			}

			// Tworzymy wszystkie pozostae opcje z obserwowanych lokacji.
			$option = $(tpl(location));
			if (location.slug === slug) {
				$option.attr('selected', 'selected');
			}
			$select.append($option);
		});

		// Uzupeniamy url-e w odnonikach
		$('.bouncy-option').each(function () {
			this.href = createUrl(slug, $(this).attr('data-content'));
		});
		$select.on('change', switchOptions);

		// Upewniamy si, e mamy co pokaza.
		if ($select.find('option').length) {
			$select.show();
		}
	});
}

// Widget uzupeniajcy bouncy-menu. Pozwala nam dynamicznie
// podmienia linki opcji w zalenoci od wybranego miejsca.

$.fn.bouncyMenu = function () {
	return $(this).each(function () {
		createMenu($(this));
	});
};

});

define("js/modules/bouncy-navigation/bouncy-menu", function(){});

